function image_data=rd_img8(filename,tot)% Reads Fluoview Tiff image series% By DK a long time agoinfo = imfinfo(filename);% tot=size(info,2));image_data = uint8(zeros(info(1).Height,info(1).Width,tot)); % preallocate 3-D array h = waitbar(0,'Loading image series...');for frame=1:tot    [image_data(:,:,frame),map] = imread(filename,frame);    waitbar(frame/tot,h)endclose(h)